     function getDataBiodataSM()
     {
          $month         = $this->db->escapeString($this->monthProcess);
          $year          = $this->db->escapeString($this->yearProcess);
          $next_month = $month + 1; // Format dua digit bulan (01-12)
          $next_year = $year; // Tahun tetap sama kecuali jika bulannya Desember

          // Jika bulan depan adalah Januari (bulan 1), tambahkan 1 tahun
          if ($next_month == 13) {
              $next_month = 1;
              $next_year += 1;
          }

          $startdate     = "{$year}-{$month}-06";
          $enddate       = "{$next_year}-{$next_month}-05";


          $stQuery = "SELECT a.month_period, a.ts_id, a.year_period, a.slip_period, a.payroll_group, a.dept, a.biodata_id, a.full_name, a.due_date, b.gender, a.marital_status, b.tax_no, a.position, b.id_card_address, c.monthly FROM tr_slip a, mt_biodata_01 b, mt_salary c WHERE a.biodata_id = b.biodata_id AND a.biodata_id AND c.biodata_id AND a.due_date BETWEEN '".$startdate."' AND '".$enddate."' GROUP BY a.biodata_id; ";
          // echo $stQuery;
          // exit();
          $query = $this->db->query($stQuery)->getResultArray();
          
          return $query;
     }

