     function cek_duedate($sm)
     {
          $stQuery = "SELECT a.payroll_group,b.due_date FROM tr_slip b, tr_timesheet a WHERE a.ts_id=b.ts_id AND a.month_process = '".$this->db->escapeString($this->monthProcess)."' AND a.year_process = '".$this->db->escapeString($this->yearProcess)."' AND a.start_date = '".$this->db->escapeString($this->startDate)."' AND b.due_date NOT IN ('0000-00-00','')";

          if ($sm != 'all') {
               $stQuery .= " AND a.payroll_group = '".$sm."'";
          }
          $stQuery .= " Group BY b.due_date ";

          $query = $this->db->query($stQuery)->getResultArray();
          return $query;
     }

